const header=document.querySelector(".header"),toggler=document.querySelector(".header-toggle"),body=document.querySelector(".page-body");toggler.addEventListener("click",(()=>{header.classList.contains("header--nav-opened")?(header.classList.remove("header--nav-opened"),body.classList.remove("page-body--hidden"),header.classList.add("header--nav-closed")):(header.classList.add("header--nav-opened"),body.classList.add("page-body--hidden"),header.classList.remove("header--nav-closed"))}));